import React, { useState } from 'react';
import './index.css';

function InputComponent() {
    const [text,setText] = useState('akjshkd')
    const onChangeText = (e:any) => {
        setText(e.target.value)
    }

  return (
    <div className="rootContainer">
    <input className="todo-textField" type="text" value={text} placeholder="Enter your todo here" onChange={onChangeText}/>
    <button className="todo-Button" onClick> Add</button>
    </div>
  );
}

export default InputComponent;
